mapper.delete:
		 {call
			declare
        	begin
        		delete from s_attach_file 
        	 	 where uuid in (
        	 	       select uuid
        	 	         from sm_post_attach
        	 	        where post_id = #{postId}
        			   );
				delete from sm_post_attach where post_id = #{postId};
            end
    	 }
